🟢 index.html🔵 datos.php <pre><code>&lt;!DOCTYPE html&gt;
&lt;html lang="en"&gt;
&lt;head&gt;
    &lt;meta charset="UTF-8"&gt;
    &lt;meta name="viewport" content="width=device-width, initial-scale=1.0"&gt;
    &lt;link rel="stylesheet" href="style.css"&gt;
    &lt;title&gt;Formulario con PHP 🚀&lt;/title&gt;
&lt;/head&gt;
&lt;body&gt;
    &lt;form
        action="datos.php"
        method="post"
    &gt;
        &lt;input
            type="text"
            id="nombre"
            name="nombre"
            placeholder="Introduce tu nombre"
        &gt;
        &lt;input
            type="email"
            id="email"
            name="email"
            placeholder="Introduce tu correo"
        &gt;
        &lt;input
            type="password"
            id="password"
            name="password"
        &gt;

        &lt;select name="aeropuertos" id=""&gt;
            &lt;option value=""&gt;-- Selecciona una opción&lt;/option&gt;
            &lt;option value="ALC"&gt;Alicante&lt;/option&gt;
            &lt;option value="BCN"&gt;Barcelona&lt;/option&gt;
            &lt;option value="MAD"&gt;Madrid&lt;/option&gt;
        &lt;/select&gt;

        &lt;input type="submit" value="Enviar"&gt;

    &lt;/form&gt;
&lt;/body&gt;
&lt;/html&gt;
</code></pre> <pre><code>    &lt;?php
      $nombre = $_POST["nombre"];
      $email = $_POST["email"];
      $password = $_POST["password"];
      $aeropuertos = $_POST["aeropuertos"];

      htmlElement('p', 'Nombre: ', $nombre);
      htmlElement('p', 'E-mail: ', $email);
      htmlElement('p', 'Password: ', $password);
      htmlElement('p', 'Aeropuerto: ', $aeropuertos);

      function htmlElement($tag, $label, $cadena){
          echo '&lt;' . $tag . '&gt;';
          echo $label . $cadena;
          echo '&lt;/' . $tag . '&gt;';
      }
  ?&gt;
</code></pre>
